mapper.select:
/* boardCount :: 메인 게시물 카운트 */ SELECT COUNT(*) FROM tb_brd WHERE DEL_YN = 'N'
AND TITLE LIKE CONCAT('%', #{searchText}, '%') AND CONTENT LIKE CONCAT('%', #{searchText}, '%')
AND TITLE LIKE CONCAT('%', #{searchText}, '%')
AND CONTENTS LIKE CONCAT('%', #{searchText}, '%')
AND REG_ID LIKE CONCAT('%', #{searchText}, '%')
limit #{scopeRow}, #{rowCount}

/* boardIndo :: 게시판 상세 조회 */
SELECT *
FROM tb_brd
WHERE SEQ = #{seq}

mapper.update:
/* indexUpdate :: 게시판 수정 */
UPDATE tb_brd SET
	TITLE = #{title}
	, CONTENT = #{contents}
	, UPD_DT = NOW()
WHERE SEQ = #{seq}

mapper.insert:
/* indexWrite :: 게시판 등록 */
INSERT INTO tb_brd SET
	SEQ = #{seqArr}
	, TITLE = #{title}
	, CONTENT = #{contents}
	, REG_ID = #{adminId}
	, REG_DT = NOW()
